}

#main {
    background-color: #000;
}
body {
	font-family: Segoe UI,Helvetica Neue,Helvetica,Lucida Grande,Arial,Ubuntu,Cantarell,Fira Sans,sans-serif;
	color: #4a4a4a;
	text-rendering: optimizeLegibility;
	font-feature-settings: "kern";
	-webkit-font-feature-settings: "kern";
	-moz-font-feature-settings: "kern";
	-moz-font-feature-settings: "kern=1";
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	/* background-color: #e0e0e0; */
	background-image: linear-gradient(180deg,#696969,#d2dbdc);
}

sourceCode:
/* ==UserStyle==
@name WhatsApp DeepDark
@namespace gitlab.com/RaitaroH/WhatsApp-DeepDark
@homepageURL https://gitlab.com/RaitaroH/WhatsApp-DeepDark
@version 1.4.2
@updateURL https://gitlab.com/RaitaroH/WhatsApp-DeepDark/raw/master/WhatsAppDeepDark.user.css
@description  Talk with thy friends in the dark. May the dark be kinder on thine eyes. (WhatsApp dark theme)
@author RaitaroH
@license GNU-V3.0

@preprocessor uso
@var select mainColor 'Main color' {
	"DeepDark": "#00adee",
	"BreezeDark": "#3DAEE9",
	"Vertex Dark": "#4080fb",
	"Arc Dark": "#5294E2",
	"Firefox Dark": "#5675B9",
	"Firefox57": "#4080FB",
	"Discord": "#7289DA",
	"YouTubeDark": "#E52117",
	"Mint-Y-Dark": "#9AB87C",
	"9anime": "#723f8c",
	"Black&White": "#ffffff",
	"Yellow_petrocompletions": "#FFC700",
	"Yellow2": "#FFC700",
	"Ubuntu_grey": "#EF7847",
	"Ubuntu_purple": "#EF7847",
	"Orange": "#ff6905",
	"Jisho_夜明け": "#EF7D6C",
	"Custom": "/*[[custom-main-color]]*\/"
}
@var color custom-main-color "Custom main color" "#00adee"

@var select mainBackground 'Main background color' {
	"DeepDark": "#111111",
	"BreezeDark": "#232629",
	"Vertex Dark": "#2B2B2C",
	"Arc Dark": "#343944",
	"Firefox Dark": "#272B35",
	"Firefox57": "#0C0C0D",
	"Discord": "#1E2124",
	"YouTubeDark": "#111111",
	"Mint-Y-Dark": "#2F2F2F",
	"9anime": "#0B0A0D",
	"Black&White": "#000000",
	"Yellow_petrocompletions": "#141414",
	"Yellow2": "#0a0800",
	"Ubuntu_grey": "#312D2A",
	"Ubuntu_purple": "#2C071A",
	"Orange": "#0a0400",
	"Jisho_夜明け": "#332222",
	"Custom": "/*[[custom-main-background]]*\/"
}
@var color custom-main-background "Custom main background" "#111111"

@var select secondBackground 'Second background color' {
	"DeepDark": "#181818",
	"BreezeDark": "#2a2e32",
	"Vertex Dark": "#353638",
	"Arc Dark": "#383C4A",
	"Firefox Dark": "#181D20",
	"Firefox57": "#252526",
	"Discord": "#2F3136",
	"YouTubeDark": "#232323",
	"Mint-Y-Dark": "#383838",
	"9anime": "#17151C",
	"Black&White": "#0a0a0a",
	"Yellow_petrocompletions": "#202222",
	"Yellow2": "#0c0a04",
	"Ubuntu_grey": "#3D3C38",
	"Ubuntu_purple": "#430B28",
	"Orange": "#0e0702",
	"Jisho_夜明け": "#2A1B1B",
	"Custom": "/*[[custom-second-background]]*\/"
}
@var color custom-second-background "Custom second background" "#181818"

@var select hoverBackground 'Hover background color' {
	"DeepDark": "#232323",
	"BreezeDark": "#31363b",
	"Vertex Dark": "#515254",
	"Arc Dark": "#414A59",
	"Firefox Dark": "#353A44",
	"Firefox57": "#323234",
	"Discord": "#484B51",
	"YouTubeDark": "#343434",
	"Mint-Y-Dark": "#404040",
	"9anime": "#1E1C25",
	"Black&White": "#0f0f0f",
	"Yellow_petrocompletions": "#353838",
	"Yellow2": "#0f0d05",
	"Ubuntu_grey": "#59564D",
	"Ubuntu_purple": "#520D30",
	"Orange": "#110903",
	"Jisho_夜明け": "#863B2F",
	"Custom": "/*[[custom-hover-background]]*\/"
}
@var color custom-hover-background "Custom hover background" "#232323"

@var select mainText 'Main text color' {
	"DeepDark": "#eff0f1",
	"BreezeDark": "#eff0f1",
	"Vertex Dark": "#F3F3F5",
	"Arc Dark": "#C1C8D1",
	"Firefox Dark": "#E3EEF9",
	"Firefox57": "#F9F9FA",
	"Discord": "#FFFFFF",
	"YouTubeDark": "#E1E1E1",
	"Mint-Y-Dark": "#FFFFFF",
	"9anime": "#B4B4B4",
	"Black&White": "#b4b4b4",
	"Yellow_petrocompletions": "#EFF0F1",
	"Yellow2": "#fffdf5",
	"Ubuntu_grey": "#F2F1EF",
	"Ubuntu_purple": "#F2F1EF",
	"Orange": "#fff9f5",
	"Jisho_夜明け": "#EFB26C",
	"Custom": "/*[[custom-main-text]]*\/"
}
@var color custom-main-text "main-text" "#eff0f1"

@var select dimmerText 'Secondary text color' {
	"DeepDark": "#CCCCCC",
	"BreezeDark": "#bdc3c7",
	"Vertex Dark": "#AEAFB0",
	"Arc Dark": "#B3BAC5",
	"Firefox Dark": "#606376",
	"Firefox57": "#D0D0D0",
	"Discord": "#ADA8AA",
	"YouTubeDark": "#7F7F7F",
	"Mint-Y-Dark": "#D5DADA",
	"9anime": "#747474",
	"Black&White": "#828282",
	"Yellow_petrocompletions": "#9F9999",
	"Yellow2": "#fff8e1",
	"Ubuntu_grey": "#E6E5E3",
	"Ubuntu_purple": "#E6E5E3",
	"Orange": "#ffede1",
	"Jisho_夜明け": "#986E3F",
	"Custom": "/*[[custom-dimmer-text]]*\/"
}
@var color custom-dimmer-text "dimmer-text" "#CCCCCC"

==/UserStyle== */

@-moz-document domain("whatsapp.com") {

/*Theme made by RaitaroH @https://gitlab.com/RaitaroH/WhatsApp-DeepDark*/

/*GNU General Public License v3.0*/

	/*Main color variables*/
	:root
	{
		/*User colors*/
		--main-color: /*[[mainColor]]*/;
		--main-background: /*[[mainBackground]]*/;
		--second-background: /*[[secondBackground]]*/;
		--hover-background: /*[[hoverBackground]]*/;
		--main-text: /*[[mainText]]*/;
		--dimmer-text: /*[[dimmerText]]*/;

		/*DeepDark colors*/
		/*
		--main-color: #00adee;
		--main-background: #111111;
		--second-background: #181818;
		--hover-background: #232323;
		--main-text: #eff0f1;
		--dimmer-text: #CCCCCC;
		*/

		/*KDE colors*/
		/*
		--main-color: #3DAEE9;
		--main-background: #232629;
		--second-background: #2a2e32;
		--hover-background: #31363b;
		--main-text: #eff0f1;
		--dimmer-text: #bdc3c7;
		*/

		/*Vertex Dark colors*/
		/*
		--main-color: #4080fb;
		--main-background: #2B2B2C;
		--second-background: #353638;
		--hover-background: #515254;
		--main-text: #F3F3F5;
		--dimmer-text: #AEAFB0;
		*/

		/*Arc Dark colors*/
		/*
		--main-color: #5294E2;
		--main-background: #343944;
		--second-background: #383C4A;
		--hover-background: #414A59;
		--main-text: #C1C8D1;
		--dimmer-text: #B3BAC5;
		*/

		/*Firefox Dark colors*/
		/*
		--main-color: #5675B9;
		--main-background: #272B35;
		--second-background: #181D20;
		--hover-background: #353A44;
		--main-text: #E3EEF9;
		--dimmer-text: #606376;
		*/

		/*Firefox57 Dark colors*/
		/*
		--main-color: #4080FB;
		--main-background: #0C0C0D;
		--second-background: #252526;
		--hover-background: #323234;
		--main-text: #F9F9FA;
		--dimmer-text: #D0D0D0;
		*/

		/*Discord colors*/
		/*
		--main-color: #7289DA;
		--main-background: #1E2124;
		--second-background: #2F3136;
		--hover-background: #484B51;
		--main-text: #FFFFFF;
		--dimmer-text: #ADA8AA;
		*/

		/*YouTube dark colors*/
		/*
		--main-color: #E52117;
		--main-background: #111111;
		--second-background: #232323;
		--hover-background: #343434;
		--main-text: #E1E1E1;
		--dimmer-text: #7F7F7F;
		*/

		/*Mint-Y-Dark colors*/
		/*
		--main-color: #9AB87C;
		--main-background: #2F2F2F;
		--second-background: #383838;
		--hover-background: #404040;
		--main-text: #FFFFFF;
		--dimmer-text: #D5DADA;
		*/

		/*9anime colors*/
		/*
		--main-color: #723f8c;
		--main-background: #0B0A0D;
		--second-background: #17151C;
		--hover-background: #1E1C25;
		--main-text: #B4B4B4;
		--dimmer-text: #747474;
		*/

		/*Black and white*/
		/*
		--main-color: #ffffff;
		--main-background: #000000;
		--second-background: #0a0a0a;
		--hover-background: #0f0f0f;
		--main-text: #b4b4b4;
		--dimmer-text: #828282;
		*/

		/*Yellow (colors from petrocompletions)*/
		/*
		--main-color: #FFC700;
		--main-background: #141414;
		--second-background: #202222;
		--hover-background: #353838;
		--main-text: #EFF0F1;
		--dimmer-text: #9F9999;
		*/

		/*Yellow 2*/
		/*
		--main-color: #ffc700;
		--main-background: #0a0800;
		--second-background: #0c0a04;
		--hover-background: #0f0d05;
		--main-text: #fffdf5;
		--dimmer-text: #fff8e1;
		*/

		/*Ubuntu grey colors*/
		/*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/

		/*Ubuntu purple colors*/
		/*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/

		/*Orange*/
		/*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimmer-text: #ffede1;
		*/

		/*Jisho 夜明け colors https://userstyles.org/styles/115621/jisho*/
		/*
		--main-color: #EF7D6C;
		--main-background: #332222;
		--second-background: #2A1B1B;
		--hover-background: #863B2F;
		--main-text: #EFB26C;
		--dimmer-text: #986E3F;
		*/
	}

	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*FUCK IT*/
	*, *:before, *:after
	{
		background-color: transparent !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		box-shadow: none !important;
	}
	.copyable-area, html[dir] ._3_R6X
	{
		background-color: var(--main-background) !important;
	}
	span:not([data-icon*="logo"]) svg path
	{
		fill: var(--main-text) !important;
	}
	span:not([data-icon*="logo"]) svg g path,
	span:not([data-icon*="logo"]) svg:hover path
	{
		fill: var(--main-color) !important;
	}
	button span:not([data-icon*="logo"]) svg g path, button span svg:hover g path
	{
		fill: var(--main-background) !important;
	}

	/*Login QR code*/
	html[dir] ._2EZ_m
	{
		border: 2px solid white !important;
	}
	._1lmAJ > span:nth-child(1) > svg:nth-child(1) > path:nth-child(2),
	._1lmAJ > span:nth-child(1) > svg:nth-child(1) > path:nth-child(3)
	{
		fill: black !important;
	}
	/*instruction buttons*/
	li._1Fl07:nth-child(2) > span:nth-child(1) > strong:nth-child(1) > span:nth-child(1) > span:nth-child(1) > svg:nth-child(1) > path:nth-child(2),
	li._1Fl07:nth-child(2) > span:nth-child(1) > strong:nth-child(2) > span:nth-child(1) > span:nth-child(1) > svg:nth-child(1) > path:nth-child(2)
	{
		fill: black !important;
	}

	/*Links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}
	a, a:visited
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	a:hover
	{
		opacity: 1;
	}


	/*Removing text decorations*/
	*
	{
		text-decoration: none !important;
	}

	body, html, .pane-chat-footer, #startup, #initial_startup, .backdrop.backdrop-cover, .backdrop-transparent, html[dir] ._1CnF3
	{
		background: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
		box-shadow: var(--shadow);
	}
	progress
	{
		color: var(--main-color) !important;
		background-color: var(--main-text) !important;
	}
	progress[value]::-moz-progress-bar
	{
		background-color: var(--main-color) !important;
	}
	.progress .path
	{
		stroke: var(--main-color) !important;
	}
	/*Not loggin in*/
	.popup
	{
		background-color: var(--second-background) !important;
	}
	.popup-title
	{
		color: var(--main-text) !important;
	}
	.popup hr
	{
		border-color: var(--hover-background) !important;
	}
	.popup .text-tip a, .popup .text-tip
	{
		color: var(--dimmer-text) !important;
	}
	.btn-plain, ._1qi0v
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.btn-default, html[dir] .PNlAR
	{
		background-color: var(--main-color) !important;
		opacity: .9;
		color: var(--main-text) !important;
	}
	.btn-plain:hover, .btn-default:hover, ._1qi0v:hover, html[dir] .PNlAR:hover
	{
		opacity: 1 !important;
	}


	/*Notifications*/
	html[dir] ._3YewW, html[dir] ._3YewW *
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}


	/*Intro*/
	.intro, .intro::after, html[dir] ._3qlW9, html[dir] ._3qlW9::after
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
	}
	.intro-image, [data-asset-intro-image]
	{
		background-image: url(https://gitlab.com/RaitaroH/WhatsApp-DeepDark/raw/master/Images/intro-image.png) !important;
	}
	.intro .intro-title, .iHhHL
	{
		color: var(--main-color) !important;
	}
	.hint, .media-gallery-empty, .well-chat-secondary, .empty-text, .intro .intro-text, .intro .intro-secondary-text, .drawer-blocked-hint, .qr-wrapper .wifi-hint, ._1ClcF
	{
		color: var(--main-text) !important;
	}

	/*Header*/
	.pane-header.pane-list-header, .pane-header, ._3AwwN, html[dir] ._3auIg
	{
		background-color: var(--main-background) !important;
	}
	.drawer-header .drawer-title
	{
		background-color: var(--second-background) !important;
	}
	.pane-chat-header
	{
		border-color: var(--hover-background) !important;
	}
	.chat-title, .header-title
	{
		color: var(--main-text) !important;
	}
	.Gd51Q._3sgkv
	{
		color: var(--dimmer-text) !important;
	}
	.pane-chat-header .chat-body
	{
		border: none !important;
	}


	/*Buttons*/
	button span svg path, button, [role="button"] span svg path
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
		opacity: 1 !important;
	}
	button:hover span svg path, [role="button"]:hover span svg path
	{
		fill: var(--main-color) !important;
	}
	.attach-menu-container g path, .attach-menu-container:hover g path,
	.GK4Lv:hover span svg g path
	{
		fill: var(--main-background) !important;
	}


	/*Drowpdown menus*/
	.dropdown, .dropdown-picker, html[dir] ._2uLFU, html[dir] ._1ArIP,
	._2hHc6
	{
		background-color: var(--second-background) !important;
	}
	.dropdown-item-action, ._2dGjP
	{
		color: var(--main-text) !important;
	}
	.dropdown-item-text.dropdown-item-hover, .dropdown-item-text.dropdown-item-hover .dropdown-item-action, html[dir] ._28zBA._14ou2, html[dir] ._28zBA._14ou2 ._2dGjP,
	._2yhpw:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}


	/*Sidebar*/
	.X7YrQ, html[dir] ._3Jvyf, ._2-Q3h, .ZP8RM
	{
		background-color: var(--main-background) !important;
	}
	.chatlist .chat:hover, .chatlist .chat.hover, .chatlist .chat.active, ._2UaNq:hover
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.chatlist .chat-body, .chatlist .header-body, .chatlist .chat:hover .chat-body, .chatlist .chat:hover .header-body, .chatlist .chat.hover .chat-body, .chatlist .chat.hover .header-body, .chatlist .chat:hover:after,.chatlist .chat.hover:after,.chatlist .chat.active:after, .chat-body, .header-body, .chat.active .chat-body, .chat.active .header-body
	{
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.chat:hover::after, .chat.hover::after, .chat.active::after
	{
		border: none !important;
	}
	.chat-meta, .chat-secondary, .header-secondary
	{
		color: var(--dimmer-text) !important;
	}
	.avatar
	{
		background-color: var(--hover-background) !important;
	}

	/*typing*/
	._2ZAIy
	{
		color: var(--main-color) !important;
	}

	/*notification*/
	html[dir] .OUeyt
	{
		background-color: var(--main-color) !important;
	}
	.OUeyt
	{
		color: var(--main-text) !important;
	}

	/*icons near the message*/
	.chat-status span svg path
	{
		fill: var(--main-color) !important;
	}

	/*Search*/
	._3pkkz
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
