	/* that ugly gradient for extra options*/
	._15CAo
	{
		background: var(--hover-background) !important;
	}
	/*dot*/
	html[dir] ._2IpQY .QLIwq::-moz-range-thumb
	{
		background: var(--main-color) !important;
	}
	/*progress bar*/
	html[dir] ._2IpQY .QLIwq::-moz-range-track
	{
		background: var(--dimmer-text) !important;
	}
	/*mic*/
	.VqGKQ > span:nth-child(1) > svg:nth-child(1) > path:nth-child(2)
	{
		fill: var(--main-color) !important;
		fill-opacity: 1 !important;
	}
	.VqGKQ > span:nth-child(1) > svg:nth-child(1) > path:nth-child(1)
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
	}

	/*@ theming*/
	.Uukb4
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	html[dir] ._3v_lq.j5Hcb
	{
		background-color: var(--hover-background) !important;
	}
	.message-out .matched-mention .at-symbol
	{
		color: var(--main-text) !important;
	}


	/*SVGs for audio and gifs*/
	span[data-icon="audio-file"] svg path[fill="#FFAD1F"], span[data-icon="media-gif"] svg path[fill="#263238"]
	{
		fill: var(--main-background) !important;
		fill-opacity: 1 !important;
	}
	span[data-icon="audio-file"] svg path[fill="#FFF"], span[data-icon="media-gif"] svg path[fill="#FFF"]
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
	}
	span[data-icon="audio-file"]:hover svg path[fill="#FFF"], span[data-icon="media-gif"]:hover svg path[fill="#FFF"]
	{
		fill: var(--main-color) !important;
		fill-opacity: 1 !important;
	}
	html[dir] ._1kZiz ._3geJ8::-moz-range-thumb
	{
		background: var(--main-color) !important;
	}

	/*Select messages*/
	.multi-controls, html[dir] ._2FsQ8
	{
		background-color: var(--hover-background) !important;
	}
	._1VD7W
	{
		background-color: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}

	/*Message compose*/
	.block-compose, html[dir] ._3XpR2
	{
		background-color: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	.block-compose .input-container
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
	}


	/*Forward*/
	html[dir] .iHLo1
	{
		background-color: var(--second-background) !important;
	}


	/*Emoji*/
	.menu-tabs-emoji
	{
		background-color: var(--hover-background) !important;
	}
	.menu-tabs-marker, html[dir] ._1qXED
	{
		background-color: var(--main-color) !important;
	}
	.menu-tabs-emoji .menu-tab span svg path
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
	}
	.menu-tabs-emoji .menu-tab:hover span svg path
	{
		fill: var(--main-color) !important;
	}
	.compose-box-items-overlay-container, .emoji-search-row-input, .emoji-panel-body-container, .emoji-search, .dropdown-picker, .dropdown-picker .dropdown-nib, .emoji-panel-body-container, .emoji-search-row
	{
		background: var(--hover-background) !important;
	}
	.emoji-search-row, .emoji-panel-body
	{
		background: var(--second-background) !important;
	}
	.emoji-panel-title, input.input-emoji-search, ._3KEhc
	{
		color: var(--dimmer-text) !important;
	}
	.compose-box-items-overlay-container
	{
		border-color: var(--hover-background) !important;
	}
	.emojik.c0:hover, .emojik.c1:hover, .emojik.c2:hover, .emojik.c3:hover, .emojik.c4:hover, .emojik.c5:hover, .emojik.c6:hover, .emojik.c7:hover
	{
		filter: brightness(120%);
	}
	/*Tried to make the background of the emoji a bit less noticable*/
	*.emojik
	{
		border-radius: 100% !important;
	}
	/*gif*/
	html[dir] ._2s3PU
	{
		background-color: var(--hover-background) !important;
	}

	.dropdown-emoji.dropdown-inverse-vertical .dropdown-nib
	{
		background-color: var(--hover-background) !important;
	}

	/*Date*/
	.message-system
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}

	/*Retry*/
	html[dir] ._3dGYA
	{
		background-color: var(--hover-background) !important;
	}
	html[dir] ._3dGYA:hover span svg path
	{
		fill: var(--main-color) !important;
	}
	.Qf313
	{
		stroke: var(--main-text) !important;
	}

	/*Not read*/
	html[dir] .L89LI
	{
		background-color: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	html[dir] ._1mq8g
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	/*nr of messages unread*/
	.P6z4j
	{
		background-color: var(--main-color) !important;
	}

	/*Go down*/
	.incoming-msgs
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.incoming-msgs span svg path
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
	}
	.incoming-msgs:hover span svg path
	{
		fill: var(--main-color) !important;
	}


	/*Image view*/
	.media-viewer, .media-panel-header, .header-body, ._1HjMu, .VIAHn, .cGLoy
	{
		background-color: var(--main-background) !important;
	}
	.media-content .media-caption, .media-chat .chat-main, .media-chat .header-main, .text-input .label-text
	{
		color: var(--dimmer-text) !important;
	}
	.drawer-header-offset, .btn-fill, .send-logs-screenshots .send-logs-screenshots-action
	{
		color: var(--main-text) !important;
	}
	.media-content .btn-round, .drawer-header-offset, .media-collection
	{
		background-color: var(--hover-background) !important;
	}
	span[title="Close"] svg path, .btn-round span svg path, .media-panel-tools .menu-item span svg path, .btn-fill span svg path
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
	}
	span[title="Close"]:hover svg path, .btn-round:hover span svg path, .media-panel-tools .menu-item:hover span svg path, .btn-fill:hover span svg path, html[dir] ._3hV1n:hover span svg path
	{
		fill: var(--main-color) !important;
		fill-opacity: 1 !important;
	}
	.media-viewer-thumbs-container, .media-thumb.active .media-thumb-body
	{
		border-color: var(--hover-background) !important;
	}
	.btn-fill, .send-logs-screenshots .send-logs-screenshots-action
	{
		background-color: var(--second-background) !important;
	}
	.btn-fill:hover
	{
		color: var(--main-color) !important;
	}
	html[dir] ._3hV1n
	{
		background-color: var(--main-background) !important;
	}

	/*Add a caption*/
	._7HWvs
	{
		color: var(--dimmer-text) !important;
	}
	/*Border*/
	html[dir] ._2Mg6D .CzI8E
	{
		border-color: var(--main-color) !important;
	}

	/*Send contacts*/
	.drawer-header-popup, .drawer-controls-summary
	{
		background-color: var(--main-background) !important;
		color: var(--main-text) !important;
	}

	/*Starred*/
	.starred-msg-wrapper.current, .starred-msg-wrapper.active, .starred-msg-wrapper.active::after, .starred-msg-wrapper.active::before
	{
		background-color: var(--hover-background) !important;
	}
	.starred-title-meta
	{
		color: var(--dimmer-text) !important;
	}
	.starred-chevron-icon span svg path
	{
		fill: var(--main-text) !important;
		fill-opacity: 1 !important;
	}
	.starred-chevron-icon:hover span svg path
	{
		fill: var(--main-color) !important;
		fill-opacity: 1 !important;
	}

	/*Undo notification*/
	.z5_OS
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.z5_OS:hover
	{
		opacity: 1 !important;
	}


	/*Reply*/
	.compose-popup-panel, .compose-popup-panel .quoted-msg-status, .btn-background
	{
		background-color: var(--hover-background) !important;
	}
	.quoted-msg-text, .emojitext.quoted-mention
	{
		color: var(--dimmer-text) !important;
	}
	.color-1
	{
		color: var(--main-color) !important;
	}
	.bg-color-1
	{
		background-color: var(--main-color) !important;
	}
	._3FwEk span svg path
	{
		fill: var(--main-text) !important;
	}

	/*Message info*/
	.drawer-header-small, .drawer-info .chat-body:hover, html[dir] ._2zkF3
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.pane-preview, .chatlist.chatlist-group-msg, .drawer-info .chat-body, .drawer-info .chat
	{
		background-color: var(--second-background) !important;
	}
	.drawer-header-small
	{
		background-color: var(--main-background) !important;
	}
	.chatlist.chatlist-group-msg .drawer-section-title, .chatlist.chatlist-group-msg .drawer-section-footer, .chatlist.chatlist-group-msg .chat:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.group-msg-info-title-icon span svg path, .drawer-info .chat-body span[data-icon="status-dblcheck"] svg path
	{
		fill: var(--main-text) !important;
	}
	.drawer-info .chat-body span[data-icon="status-dblcheck-ack"] svg path
	{
		fill: var(--main-color) !important;
		fill-opacity: 1 !important;
	}
	.drawer-section-info .chat:not(:last-child), html[dir] ._3YwRO
	{
		border-color: var(--hover-background) !important;
	}


	/*Group admin*/
	.contact-marker-admin
	{
		border-color: var(--main-color) !important;
		color: var(--main-color) !important;
	}


	/*Default avatar svgs*/
	div span[data-icon="default-user"] svg path[fill="#DFE5E7"], div span[data-icon="default-group"] svg path[fill="#DFE5E7"]
	{
		fill: var(--second-background) !important;
	}
	div span[data-icon="default-user"] svg g[fill="#FFF"] path, div span[data-icon="default-group"] svg path[fill="#FFF"]
	{
		fill: var(--main-color) !important;
	}


	/*Loading svg*/
	circle
	{
		stroke: var(--main-text) !important;
	}

sourceCode:
/* ==UserStyle==
@name DuckDuckGo DeepDark
@namespace gitlab.com/RaitaroH/DuckDuckGo-DeepDark
@homepageURL https://gitlab.com/RaitaroH/DuckDuckGo-DeepDark
@version 1.9.3
@updateURL https://gitlab.com/RaitaroH/DuckDuckGo-DeepDark/raw/master/DuckDuckGoDeepDark.user.css
@description Light will blind you in your search for answers, dark will make them shine. May the dark be kinder on thine eyes. (DuckDuckGo dark theme)
@author RaitaroH
@license GNU-V3.0

@preprocessor stylus

@var select stylus-deepdark-style "Preset themes" [
	"Deep-Dark",
	"Inspired-Dark",
	"Breeze-Dark",
	"HavocOS",
	"Arc-Dark",
	"Vertex-Dark",
	"Mint-Y-Dark",
	"Adapta-Nokto",
	"Adapta-Breath-Nokto",
	"Gruvbox-Dark",
	"Gruvbox-Light",
	"NierAutomata-Dark",
	"NierAutomata-Light",
	"Solarized-Dark",
	"Solarized-Light",
	"9anime",
	"Firefox-Dark",
	"Firefox-57",
	"Discord",
	"YouTube-Dark",
	"Black-and-White",
	"Yellow",
	"Yellow-2",
	"Ubuntu-Grey",
	"Ubuntu-Purple",
	"Orange",
	"Jisho",
	"Custom"
]

@var color mainColor "Custom main color" "#367bf0"
@var color mainBackground "Custom main background" "#22242d"
@var color secondBackground "Custom second background" "#242730"
@var color hoverBackground "Custom hover background" "#4e5467"
@var color mainText "Custom main text" "#eee"
@var color dimmerText "Custom second text" "#ccc"
@var color colorShadow "Custom shadow color" "rgba(56, 60, 74, .3)"

==/UserStyle== */

@-moz-document regexp("https?:\/\/(?!help\.)(.*duckduckgo.com|3g2upl4pq6kufc4m.onion)\/(?!donations).*$") {

/*Theme made by RaitaroH @https://gitlab.com/RaitaroH/DuckDuckGo-DeepDark*/

/*GNU General Public License v3.0*/

	/*Main color variables*/
	:root
	{
		if stylus-deepdark-style == "Deep-Dark" {
			--main-color: #00adee;
			--main-background: #111;
			--second-background: #181818;
			--hover-background: #232323;
			--main-text: #eff0f1;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .3);
		}
		else if stylus-deepdark-style == "Inspired-Dark" {
			--main-color: #5e8acc;
			--main-background: #232629;
			--second-background: #181818;
			--hover-background: #515254;
			--main-text: #eee;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Breeze-Dark" {
			--main-color: #3daee9;
			--main-background: #232629;
			--second-background: #2a2e32;
			--hover-background: #31363b;
			--main-text: #eff0f1;
			--dimmer-text: #bdc3c7;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Vertex-Dark" {
			--main-color: #4080fb;
			--main-background: #2b2b2c;
			--second-background: #353638;
			--hover-background: #515254;
			--main-text: #f3f3f5;
			--dimmer-text: #aeafb0;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Arc-Dark" {
			--main-color: #5294e2;
			--main-background: #343944;
			--second-background: #383c4a;
			--hover-background: #414a59;
			--main-text: #c1c8d1;
			--dimmer-text: #b3bac5;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Firefox-Dark" {
			--main-color: #5675b9;
			--main-background: #272b35;
			--second-background: #181d20;
			--hover-background: #353a44;
			--main-text: #e3eef9;
			--dimmer-text: #bec0cc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Firefox-57" {
			--main-color: #4080fb;
			--main-background: #0c0c0d;
			--second-background: #252526;
			--hover-background: #323234;
			--main-text: #f9f9fa;
			--dimmer-text: #d0d0d0;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .2);
		}
		else if stylus-deepdark-style ==  "Discord" {
			--main-color: #7289da;
			--main-background: #1e2124;
			--second-background: #2f3136;
			--hover-background: #484b51;
			--main-text: #fff;
			--dimmer-text: #ada8aa;
			--shadow: 0 1px 0.5px rgba(47, 49, 54, .23);
		}
		else if stylus-deepdark-style == "YouTube-Dark" {
			--main-color: #e52117;
			--main-background: #111;
			--second-background: #232323;
			--hover-background: #343434;
			--main-text: #e1e1e1;
			--dimmer-text: #7f7f7f;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .2);
		}
		else if stylus-deepdark-style == "Mint-Y-Dark" {
			--main-color: #9ab87c;
			--main-background: #2f2f2f;
			--second-background: #383838;
			--hover-background: #404040;
			--main-text: #fff;
			--dimmer-text: #d5dada;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "9anime" {
			--main-color: #723f8c;
			--main-background: #0b0a0d;
			--second-background: #17151c;
			--hover-background: #1E1c25;
			--main-text: #f9f6fb;
			--dimmer-text: #cac0cf;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .13);
		}
		else if stylus-deepdark-style == "Black-and-White" {
			--main-color: #fff;
			--main-background: #000;
			--second-background: #1e1e1e;
			--hover-background: #313131;
			--main-text: #fff;
			--dimmer-text: #aaa;
			--shadow: 0 1px 0.5px rgba(54 ,54 ,54, .2);
		}
		else if stylus-deepdark-style == "Yellow" {
			--main-color: #ffc700;
			--main-background: #141414;
			--second-background: #202222;
			--hover-background: #353838;
			--main-text: #eff0f1;
			--dimmer-text: #9f9999;
			--shadow: 0 1px 0.5px rgba(34, 34, 34, .2);
		}
		else if stylus-deepdark-style == "Yellow-2" {
			--main-color: #ffc700;
			--main-background: #0a0800;
			--second-background: #0c0a04;
			--hover-background: #0f0d05;
			--main-text: #fffdf5;
			--dimmer-text: #fff8e1;
			--shadow: 0 1px 0.5px rgba(34, 34, 34, .2);
		}
		else if stylus-deepdark-style == "Ubuntu-Grey" {
			--main-color: #ef7847;
			--main-background: #312d2a;
			--second-background: #3d3c38;
			--hover-background: #59564d;
			--main-text: #f2f1ef;
			--dimmer-text: #e6e5e3;
			--shadow: 0 1px 0.5px rgba(89, 86, 77, .12);
		}
		else if stylus-deepdark-style == "Ubuntu-Purple" {
			--main-color: #ef7847;
			--main-background: #2c071a;
			--second-background: #430b28;
			--hover-background: #520D30;
			--main-text: #f2f1ef;
			--dimmer-text: #e6e5e3;
			--shadow: 0 1px 0.5px rgba(82, 13, 48, .2);
		}
		else if stylus-deepdark-style == "Orange" {
			--main-color: #ff6905;
			--main-background: #0a0400;
			--second-background: #0e0702;
			--hover-background: #110903;
			--main-text: #fff9f5;
			--dimmer-text: #ffede1;
			--shadow: 0 1px 0.5px rgba(255, 105, 5, .1);
		}
		else if stylus-deepdark-style == "Jisho" {
			--main-color: #ef7d6c;
			--main-background: #332222;
			--second-background: #2a1b1b;
			--hover-background: #863b2f;
			--main-text: #EFB26C;
			--dimmer-text: #986E3F;
			--shadow: 0 1px 0.5px rgba(37, 19, 5, .19);
		}
		else if stylus-deepdark-style == "Adapta-Nokto" {
			--main-color: #00bcd4;
			--main-background: #222d32;
			--second-background: #263238;
			--hover-background: #2a353b;
			--main-text: #fff;
			--dimmer-text: #9b9b9b;
			--shadow: 0 1px 0.5px  rgba(61, 77, 86, .2);
		}
		else if stylus-deepdark-style == "Adapta-Breath-Nokto" {
			--main-color: #1abc9c;
			--main-background: #222d32;
			--second-background: #263238;
			--hover-background: #2a353b;
			--main-text: #fff;
			--dimmer-text: #9b9b9b;
			--shadow: 0 1px 0.5px  rgba(42, 53, 59, .32);
		}
		else if stylus-deepdark-style == "Solarized-Dark" {
			--main-color: #268bd2;
			--main-background: #073642;
			--second-background: #03303c;
			--hover-background: #002b36;
			--main-text: #fdf6E3;
			--dimmer-text: #eee8d5;
			--shadow: 0 1px 0.5px  rgba(0, 43, 54, .3);
		}
		else if stylus-deepdark-style == "Solarized-Light" {
			--main-color: #268bd2;
			--main-background: #fdf6e3;
			--second-background: #f5efdc;
			--hover-background: #eee8d5;
			--main-text: #073642;
			--dimmer-text: #586e75;
			--shadow: 0 1px 0.5px  rgba(222, 216, 196, .2);
		}
		else if stylus-deepdark-style == "Gruvbox-Dark" {
			--main-color: #fe8019;
			--main-background: #1d2021;
			--second-background: #282828;
			--hover-background: #3c3836;
			--main-text: #fbf1c7;
			--dimmer-text: #ebdbb2;
			--shadow: 0 1px 0.5px  rgba(60, 56, 54, .22);
		}
		else if stylus-deepdark-style == "Gruvbox-Light" {
			--main-color: #af3a03;
			--main-background: #f9f5d7;
			--second-background: #fbf1c7;
			--hover-background: #ebdbb2;
