  body.zh_logged_in .zhc-selection-item:hover .zhc-icon:not([class$="-red"]):not([class$="-white"]):not([class$="-light"]):not([class$="-purple"]),
  body.zh_logged_in [class*="zhc-image--placeholder"] {
